mapper.select:
select *, video_file AS videoFile
from video
where no = #{no}

select *
from review
where video_no=#{video_no}
  and review_no=#{no}
order by review_no asc

select count(*)
from review
where video_no = #{video_no}
  and review_no = #{no}

mapper.update:
update video
set views = ifnull(views, 0) + 1
where no = #{no}
